mapper.select:
SELECT id, phone, password, login_type as loginType, open_id as openId, nickname, avatar_url AS avartarUrl, `level`, birthday, gender, gmt_last_login AS gmtLastLogin, last_login_ip AS lastLoginIp, `status`, gmt_update AS gmtUpdate, gmt_create AS gmtCreate FROM kxmall_user WHERE 1 = 1
AND phone like concat(concat("%",#{phone}),"%")
AND nickname like concat(concat("%",#{nickname}),"%")
AND `level` = #{level}
AND gender = #{gender}
AND status = #{status}
limit #{offset},#{limit}

SELECT count(1) FROM kxmall_user WHERE 1 = 1
AND phone like concat(concat("%",#{phone}),"%")
AND nickname like concat(concat("%",#{nickname}),"%")
AND `level` = #{level}
AND gender = #{gender}
AND status = #{status}

SELECT count(DISTINCT(o.user_id)) as num FROM `kxmall_order` o
left join kxmall_user u on o.user_id = u.id
where u.gmt_create between #{start} and #{start}
and o.gmt_create between #{start} and #{start}
