mapper.select:
SELECT count(1) FROM `kxmall_stock` s left join kxmall_sku sku on s.sku_id = sku.id left JOIN kxmall_spu spu on s.spu_id = spu.id LEFT JOIN kxmall_category ca on spu.category_id = ca.id left join kxmall_storage sto on sto.id = s.storage_id WHERE 1 = 1
and s.storage_id = #{storageId}
and ca.id = #{categoryId}
and ( spu.id like CONCAT('%',#{name},'%') or spu.title like CONCAT('%',#{name},'%') or sku.bar_code like CONCAT('%',#{name},'%') )
and s.status = #{status}
and sku.bar_code not in
#{id}

SELECT s.id, s.storage_id as storageId, sto.name as name, ca.id as categoryId, sku.bar_code as barCode, s.spu_id as spuId, s.sku_id as skuId, s.frezz_stock as frezzStock, spu.title as spuTitle, sku.title as skuTitle, s.status as status, s.stock as stock, s.sales, spu.unit as unit, sku.original_price as originalPrice, s.price as price, s.gmt_create as gmtCreate, s.gmt_update as gmtUpdate FROM `kxmall_stock` s left join kxmall_sku sku on s.sku_id = sku.id left JOIN kxmall_spu spu on s.spu_id = spu.id LEFT JOIN kxmall_category ca on spu.category_id = ca.id left join kxmall_storage sto on sto.id = s.storage_id WHERE 1 = 1
and s.storage_id = #{storageId}
and ca.id = #{categoryId}
and ( spu.id like CONCAT('%',#{name},'%') or spu.title like CONCAT('%',#{name},'%') or sku.bar_code like CONCAT('%',#{name},'%') )
and s.status = #{status}
and s.id in
#{id}
and sku.bar_code not in
#{id}
limit #{offset},#{size}

SELECT * FROM kxmall_stock stock
LEFT JOIN kxmall_sku sku
on stock.sku_id = sku.id
where stock.storage_id = #{storageId}
and sku.bar_code = #{barCode}

select sto.id as 'stockDto.id',sto.name as 'storageDto.name',sto.id as 'storageDto.id',ca.title as 'categoryDto.title',sk.bar_code as 'skuDto.barCode',
sp.id,sp.title,sk.title as 'skuDto.title',(st.frezz_stock+st.stock) as 'stockDto.nowStock',
st.stock as 'stockDto.stock',st.frezz_stock as 'stockDto.frezzStock',st.warning_num as 'stockDto.warningNum',sp.unit
from kxmall_stock st
left join kxmall_spu sp on sp.id = st.spu_id
left join kxmall_sku sk on sk.spu_id = st.spu_id
LEFT JOIN kxmall_storage sto on sto.id = st.storage_id
LEFT JOIN kxmall_category ca on ca.id = sp.category_id
where st.status = 1

    and st.storage_id = #{storageId}


    and sp.category_id = #{categoryId}


    and sp.category_id in

        #{list}



    and ( sp.id like CONCAT('%',#{name},'%') or sp.title like CONCAT('%',#{name},'%')  or sk.bar_code like CONCAT('%',#{name},'%') )



        and (st.frezz_stock + st.stock) >= ${minNum}

        and  (st.frezz_stock + st.stock) <= ${maxNum}



        and st.stock >= ${minNum}

        and  st.stock <= ${maxNum}



        and st.frezz_stock >= ${minNum}

        and  st.frezz_stock <= ${maxNum}





        and st.stock <= st.warning_num

select count(st.id)
from kxmall_stock st
left join kxmall_spu sp on sp.id = st.spu_id
left join kxmall_sku sk on sk.spu_id = st.spu_id
LEFT JOIN kxmall_storage sto on sto.id = st.storage_id
LEFT JOIN kxmall_category ca on ca.id = sp.category_id
where st.status = 1

    and st.storage_id = #{storageId}


    and sp.category_id = #{categoryId}


    and sp.category_id in

        #{list}



    and ( sp.id like CONCAT('%',#{name},'%') or sp.title like CONCAT('%',#{name},'%')  or sk.bar_code like CONCAT('%',#{name},'%') )



        and (st.frezz_stock + st.stock) >= ${minNum}

        and  (st.frezz_stock + st.stock) <= ${maxNum}



        and st.stock >= ${minNum}

        and  st.stock <= ${maxNum}



        and st.frezz_stock >= ${minNum}

        and  st.frezz_stock <= ${maxNum}





        and st.stock <= st.warning_num
